import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { getSingleCourse } from "../../Helpers/Courses";

export const CoursesPost = () => {
  const { coid } = useParams();

  const [course, setCourse] = useState({});
  const [err, setErr] = useState(null);

  useEffect(() => {
    getSingleCourse(coid).then((data) => {
      if (data.err) {
        setErr(data.err);
        console.log(data.err);
      } else {
        setCourse(data.course);
      }
    }).catch((err) => err);
  }, []);

  console.log(course);
  return (
    <div>
      <div className="container">
        <div className="div img img-fluid">
          <img
            src={`http://localhost:4000/${course.image}`}
            className="img img-fluid"
            alt=""
          />
        </div>

        <div className="d-flex justify-content-center">
          <h4 className="text-center text-bold">{course.name}</h4>
          <h6>{course.description}</h6>
        </div>
      </div>
    </div>
  );
};
