import React, { useEffect, useState } from "react";
import { getAllWorkshop } from "../../Helpers/Workshop";
import { getConf } from "../../Helpers/Conf";
import { getCourses } from "../../Helpers/Courses";
import Card from "../../Components/Card";

export const UnAuthHome = () => {
  const [workshops, setWorkshops] = useState([]);
  const [conf, setConf] = useState([]);
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState([]);

  useEffect(() => {
    getAllWorkshop().then((data) => {
      if (data.err) {
        setError(data.err);
      } else {
        setWorkshops(data.workshops);
      }
    }).catch();

    getConf().then((data) => {
      if (data.err) {
        setError(data.err);
      } else {
        setConf(data.conferences);
      }
    }).catch();

    getCourses().then((data) => {
      if (data.err) {
        setError(data.err);
      } else {
        setCourses(data.Course);
      }
    }).catch((err) => console.log(err));
  }, []);

  const wo = [workshops[0], workshops[1], workshops[2]];
  console.log("Array WO", wo);

  return <div>
    <div className="container">
      <h1>Workshops</h1>

      <div className="container">
        <div className="row">
          {wo.map((w, index) => {
            console.log(w, index);
            console.log(w[0].name);
            // return (<React.Fragment key={index}>
            //   <div className="col-md-4 my-3">
            //     <Card
            //       name={w[index].name}
            //       description={w[index].description}
            //       price={w[index].price}
            //       date={w[index].dateOfWorkshop}
            //       id={w[index]._id}
            //     />
            //   </div>
            // </React.Fragment>);
          })}
        </div>
      </div>

      <h1>Conferences</h1>
      <div className="container">
        <div className="row">
          {conf.map((c, index) => {
            return (<React.Fragment key={index}>
              <div className="col-md-4 my-3">
                <Card
                  name={c.name}
                  description={c.description}
                  price={c.price}
                  date={c.dateOfWorkshop}
                  id={c._id}
                />
              </div>
            </React.Fragment>);
          })}
        </div>
      </div>

      <h1>Courses :</h1>
      <div className="container">
        <div className="row">
          {courses.map((co, index) => {
            return (
              <React.Fragment key={index}>
                <div className="col-md-4 my-3">
                  <Card
                    name={co.name}
                    description={co.description}
                    price={co.price}
                    date={co.dateOfWorkshop}
                    id={co._id}
                  />
                </div>
              </React.Fragment>
            );
          })}
        </div>
      </div>
    </div>
  </div>;
};
