import React, { useEffect, useState } from "react";
import { getAllWorkshop } from "../../Helpers/Workshop";
import { getConf } from "../../Helpers/Conf";
import { getCourses } from "../../Helpers/Courses";
import Card from "../../Components/Card";

export const UnAuthHome = () => {
  const [workshops, setWorkshops] = useState([]);
  const [conf, setConf] = useState([]);
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState([]);

  useEffect(() => {
    getAllWorkshop().then((data) => {
      if (data.err) {
        setError(data.err);
      } else {
        setWorkshops(data.workshops);
      }
    }).catch();

    getConf().then((data) => {
      if (data.err) {
        setError(data.err);
      } else {
        setConf(data.conferences);
      }
    }).catch();

    getCourses().then((data) => {
      if (data.err) {
        setError(data.err);
      } else {
        setCourses(data.Course);
      }
    }).catch((err) => console.log(err));
  }, []);

  console.log("Conferences", conf);
  console.log("Workshop", workshops);
  console.log("Course", courses);

  return <div>
    <h1>Workshops</h1>

    <div className="container">
      <div className="row">
        {workshops.map((w, index) => {
          return (<React.Fragment key={index}>
            <div className="col-md-4 my-3"><Card /></div>
          </React.Fragment>);
        })}
      </div>
    </div>
  </div>;
};
